import React, { Component } from 'react'
import { NavBar,Icon,  Carousel,Tabs, WhiteSpace, Badge,Grid,WingBlank} from 'antd-mobile';

export default class Idea extends Component {
    render() {
        return (
            <div>
                <NavBar style={{backgroundColor:'#3fcccb',color:'white'}}
                    rightContent={[
                        <Icon key='0' type='search' style={{marginRight:'16px'}} />
                    ]}
                >灵感
                </NavBar>
                
                <Tabs tabs={tabs}
                    initialPage={1}
                    onChange={(tab, index) => { console.log('onChange', index, tab); }}
                    onTabClick={(tab, index) => { console.log('onTabClick', index, tab); }}
                    >
                    <div style={{alignItems: 'center', justifyContent: 'center', backgroundColor: '#fff' }}>
                        <Grid data={data1}
                            columnNum={2}
                            renderItem={dataItem => (
                                <div style={{height:'100%',marginLeft:'15px',marginRight:'15px'}}>
                                    <img src={dataItem.icon} style={{ width: '100%', height: '100%' }} alt="" />
                                </div>
                            )}
                            />
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        冬季
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        宜家
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        小清新
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        小户型
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        个性色彩
                    </div>
                </Tabs>
                <WhiteSpace />
            </div>
        )
    }
}
