import React, { Component } from 'react'

export default class Person extends Component {
    constructor(){
        super();
        this.state={
            data:[1,2,3]
        }
    }
    delItem = (idx) => {
        this.setState((state,props)=>{
            return {
                data:state.data.filter((item,index)=>idx!==index)
            }
        })
    }
    render() {
        return (
            <div>
                <div className='psup'>
                   <span>
                       管理员信息管理
                    </span> 
                    <input name='keyword' placeholder='请输入想查找的人员姓名' type='search' />
                    <button></button>
                    {/* <input name='addmanager' placeholder='点击添加管理员'></input> */}
                </div> 
                <div className='psmiddle'>
                    <input type='checkbox'  />
                    <span>姓名</span>
                    <span>性别</span>
                    <span>电话</span>
                    <span>邮箱</span>
                    <span>编辑</span>
                </div>
                <div className='psdown'>    
                {
                    this.state.data.map((item,idx)=>(
                            <li key={idx}> {item}
                            <input type='checkbox'  />
                            <span>{this.state.data.username}</span>
                            <span>{this.state.data.sex}</span>
                            <span>{this.state.tel}</span>
                            <span>{this.state.email}</span>
                            <span>
                                <button>查看</button>
                                <button onClick={()=>this.delItem(idx)} >删除</button>
                            </span>
                        </li>)
                    )
                }
                </div>
            </div>
        )
    }
}
