import { TabBar } from 'antd-mobile';
import React from 'react'
import AppHome from './AppHome';
import Shop from './Shop';
import Idea from './Idea';

export default class AppTab extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'home'
    };
  }
  render() {
    return (
      <div style={ { position: 'fixed', height: '100%', width: '100%', top: 0 } }>
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#3fcccb"
          barTintColor="white"
        >
          <TabBar.Item
            title="首页"
            key="Life"//key可以随便写  几个item下面有几栏
            icon={<i style={{fontSize:22}} className='iconfont icon-shouye'></i>
            }
            selectedIcon={
                <i style={{fontSize:22}} className='iconfont icon-shouye'></i>
            }
            selected={this.state.selectedTab === 'home'}
            onPress={() => {
              this.setState({
                selectedTab: 'home',
              });
            }}
          >
            <AppHome/>
          </TabBar.Item>
          <TabBar.Item
            icon={
              <i style={{fontSize:28}} className='iconfont icon-linggan-yuanwenjian'></i>
            }
            selectedIcon={
                <i style={{fontSize:28}} className='iconfont icon-linggan-yuanwenjian'></i>
            }
            title="灵感"
            key="Friend"
            dot
            selected={this.state.selectedTab === 'idea'}
            onPress={() => {
              this.setState({
                selectedTab: 'idea',
              });
            }}
          >
            <Idea/>
          </TabBar.Item>
          <TabBar.Item
            icon={
              <i style={{fontSize:22}} className='iconfont icon-shangcheng1'></i>
            }
            selectedIcon={
              <i style={{fontSize:22}} className='iconfont icon-shangcheng1'></i>
            }
            title="商城"
            key="Koubei"
            selected={this.state.selectedTab === 'shop'}
            onPress={() => {
              this.setState({
                selectedTab: 'shop',
              });
            }}
            data-seed="logId1"
          >
            <Shop/>
          </TabBar.Item>
          <TabBar.Item
            icon={<i style={{fontSize:22}} className='iconfont icon-wode1'></i>}
            selectedIcon={<i style={{fontSize:22}} className='iconfont icon-wode1'></i>}
            title="我的"
            key="my"
            selected={this.state.selectedTab === 'my'}
            onPress={() => {
              this.setState({
                selectedTab: 'my',
              });
            }}
          >
            我的
          </TabBar.Item>
        </TabBar>
      </div>
    );
  }
}
